package playground.develop.socialnote.utilsimport android.content.Contextimport android.net.ConnectivityManagerimport android.os.Buildimport playground.develop.socialnote.BuildConfigclass DeviceUtils(val context: Context) {    fun dsd(arg: String): Boolean {        val ds =            arrayOf(BuildConfig.dOne, BuildConfig.dTwo, BuildConfig.dThree, BuildConfig.dFour, BuildConfig.dFive)        return ds.contains(arg)    }    @Suppress("DEPRECATION")    fun isDeviceNetworkAvailable(): Boolean {        val connectivityManager =            context.getSystemService(Context.CONNECTIVITY_SERVICE) as ConnectivityManager        val networkInfo = if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.M) {            connectivityManager.activeNetwork        } else {            connectivityManager.activeNetworkInfo        }        return networkInfo != null    }    companion object {        fun getDeviceUtils(context: Context): DeviceUtils {            return DeviceUtils(context)        }    }}